import React, { Component } from 'react';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import { newExpensePr } from '../redux/actions/index';

class WalletForm extends Component {
  state = {
    id: 0,
    value: '',
    description: '',
    currency: 'USD',
    method: 'Dinheiro',
    tag: 'Alimentação',
  };

  handlechange = ({ target: { name, value } }) => {
    this.setState({
      [name]: value,
    });
  };

  submit = () => {
    const { saveExpense } = this.props;
    this.setState((state) => ({
      id: state.id + 1,
    }));
    const { id, value, description, currency, method, tag } = this.state;
    saveExpense({ id, value, description, currency, method, tag });
    this.setState({
      value: '',
      description: '',
      currency: 'USD',
      method: 'Dinheiro',
      tag: 'Alimentação',
    });
  };

  receiveExpenseEdit = () => {
    const { id, expenses } = this.props;
    const objExpense = expenses.find((element) => element.id === id);
    this.setState({
      id: objExpense.id,
      value: objExpense.value,
      description: objExpense.description,
      currency: objExpense.currency,
      method: objExpense.method,
      tag: objExpense.tag,
    });
  };

  render() {
    const { description, value, currency, method, tag } = this.state;
    const { currencies, editor } = this.props;

    return (
      <div>
        <form>
          <label htmlFor="value">
            Valor da Despesa:
            <input
              type="text"
              name="value"
              value={ value }
              data-testid="value-input"
              onChange={ this.handlechange }
            />
          </label>
          <label htmlFor="description">
            Descrição:
            <input
              type="text"
              name="description"
              value={ description }
              data-testid="description-input"
              onChange={ this.handlechange }
            />
          </label>
          <label htmlFor="currency">
            Moeda:
            <select
              aria-label="currency"
              name="currency"
              data-testid="currency-input"
              value={ currency }
              onChange={ this.handlechange }
            >
              { currencies.map((element) => <option key={ element }>{element}</option>)}
            </select>
          </label>
          <label htmlFor="method">
            Método de Pagamento:
            <select
              aria-label="method"
              name="method"
              data-testid="method-input"
              value={ method }
              onChange={ this.handlechange }
            >
              <option value="Dinheiro">Dinheiro</option>
              <option value="Cartão de crédito">Cartão de crédito</option>
              <option value="Cartão de débito">Cartão de débito</option>
            </select>
          </label>
          <label htmlFor="tag">
            Método de Pagamento:
            <select
              aria-label="tag"
              name="tag"
              data-testid="tag-input"
              value={ tag }
              onChange={ this.handlechange }
            >
              <option value="Alimentação">Alimentação</option>
              <option value="Lazer">Lazer</option>
              <option value="Trabalho">Trabalho</option>
              <option value="Transporte">Transporte</option>
              <option value="Saúde">Saúde</option>
            </select>
          </label>
        </form>
        {
          editor ? (
            <button type="button" onClick={ this.submit }>Editar despesas</button>
          ) : (
            <button type="button" onClick={ this.submit }>Adicionar despesa</button>
          )
        }
      </div>
    );
  }
}

const mapDispatchToProps = (dispatch) => ({
  saveExpense: (obj) => dispatch(newExpensePr(obj)),
});

const mapStateToProps = (state) => ({
  currencies: state.wallet.currencies,
  editor: state.wallet.editor,
  id: state.wallet.idToEdit,
  expenses: state.wallet.expenses,
});

WalletForm.propTypes = {
  currencies: PropTypes.shape(),
}.isRequired;

export default connect(mapStateToProps, mapDispatchToProps)(WalletForm);

