import React from 'react';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import { actionUserEmail, requestApi } from '../redux/actions/index';

class Login extends React.Component {
  state = {
    email: '',
    senha: '',
    buttonDisabled: true,
  };

  handleChange = ({ target: { name, value } }) => {
    this.setState({
      [name]: value,
    }, () => {
      const { email, senha } = this.state;
      const magicNumber = 5;
      if (this.validateEmail(email) && senha.length > magicNumber) {
        this.setState({ buttonDisabled: false });
      } else {
        this.setState({ buttonDisabled: true });
      }
    });
  };

  validateEmail = (email) => /\S+@\S+\.\S+/.test(email);

  entryNext = () => {
    const { history, addEmail, request } = this.props;
    const { email } = this.state;
    addEmail(email);
    request();
    history.push('/carteira');
  };

  render() {
    const { email, senha, buttonDisabled } = this.state;
    return (
      <div
        className="
      flex min-h-full items-center justify-center py-12 px-4 sm:px-6 lg:px-8"
      >
        <div className="w-full max-w-md space-y-8">
          <div>
            <img
              className="mx-auto h-12 w-auto"
              src="https://tailwindui.com/img/logos/mark.svg?color=indigo&shade=600"
              alt="Your Company"
            />
            <h2
              className="
            mt-6 text-center text-3xl font-bold tracking-tight text-gray-900"
            >
              Sign in to your account
            </h2>
            <p className="mt-2 text-center text-sm text-gray-600">
              Or
              {' '}
              <a
                href="/#"
                className="
              font-medium eftext-indigo-600 hover:text-indigo-500"
              >
                start your 14-day free trial
              </a>
            </p>
          </div>
          <form className="mt-8 space-y-6">
            <input type="hidden" name="remember" defaultValue="true" />
            <div className="-space-y-px rounded-md shadow-sm">
              <div>
                <label htmlFor="email" className="sr-only" />
                Email:
                <input
                  type="text"
                  name="email"
                  className="relative block w-full appearance-none rounded-none rounded-t-md border border-gray-300 px-3 py-2 text-gray-900 placeholder-gray-500 focus:z-10 focus:border-indigo-500 focus:outline-none focus:ring-indigo-500 sm:text-sm"
                  placeholder="Email address"
                  value={ email }
                  data-testid="email-input"
                  onChange={ this.handleChange }
                />
              </div>
              <div>
                <label htmlFor="senha" className="sr-only" />
                <input
                  type="password"
                  name="senha"
                  value={ senha }
                  data-testid="password-input"
                  className="
                  relative
                  block w-full
                  appearance-none
                  rounded-none
                  rounded-b-md border
                  border-gray-300
                  px-3
                  py-2
                  text-gray-900
                  placeholder-gray-500
                  focus:z-10
                  focus:border-indigo-500
                  focus:outline-none
                  focus:ring-indigo-500
                  sm:text-sm"
                  placeholder="Password"
                  onChange={ this.handleChange }
                />
              </div>
            </div>
            <button
              type="button"
              disabled={ buttonDisabled }
              onClick={ this.entryNext }
            >
              Entrar

            </button>
          </form>
        </div>
      </div>
    );
  }
}

const mapDispatchToProps = (dispatch) => ({
  addEmail: (value) => dispatch(actionUserEmail(value)),
  request: () => dispatch(requestApi()),
});

Login.propTypes = {
  history: PropTypes.shape({
    push: PropTypes.func,
  }),
}.isRequiered;

export default connect(null, mapDispatchToProps)(Login);
