    return (
      <div>
        <form>
          <label htmlFor="despesas">
            Valor da Despesa:
            <input
              type="number"
              name="despesas"
              value={ despesas }
              data-testid="value-input"
              onChange={ this.handlechange }
            />
          </label>
          <label htmlFor="description">
            Descrição:
            <input
              type="text"
              name="description"
              value={ description }
              data-testid="description-input"
              onChange={ this.handlechange }
            />
          </label>
          <label htmlFor="moeda">
            <select
              aria-label="moeda"
              name="moeda"
              id="moeda"
              data-testid="currency-input"
            >
              <option value="teste">teste</option>
            </select>
          </label>
        </form>
      </div>
    );
  }
}

export default WalletForm;
