import React from 'react';

class Login extends React.Component {
  state = {
    email: '',
    senha: '',
  };

  handleChange = ({ target: { name, value } }) => {
    this.setState({
      [name]: value,
    }, () => {
      validateEmail = (email) => /\S+@\S+\.\S+/.test(email);

    });
  };

  render() {
    const { email, senha } = this.state;
    return (
      <div>
        <form>
          <label htmlFor="email">
            Email:
            <input
              type="text"
              name="email"
              value={ email }
              onChange={ this.handleChange }
            />
          </label>
          <label htmlFor="email">
            Password:
            <input
              type="text"
              name="senha"
              value={ senha }
              onChange={ this.handleChange }
            />
          </label>
        </form>
      </div>
    );
  }
}

export default Login;
