import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';

class Header extends Component {
  subtotal = () => {
    const { expenses } = this.props;
    const total = expenses.reduce((acc, curr) => {
      const moeda = curr.currency;
      if (moeda !== 'USDT') {
        const value = Number(curr.exchangeRates[moeda].ask) * Number(curr.value);
        return acc + Number(value);
      }
      return acc;
    }, 0);
    return Number(total).toFixed(2);
  };

  render() {
    const { email } = this.props;
    return (
      <header className="flex items-center justify-between flex-wrap bg-purple-500 p-6">
        <div className="flex items-center flex-shrink-0 text-white mr-6">
          img
          <span className="font-semibold text-xl tracking-tight">Trybe Wallet</span>
        </div>
        <p data-testid="email-field">{email}</p>
        <p>
          Despesa Total:
          <span data-testid="total-field">{ this.subtotal()}</span>
        </p>
        <p data-testid="header-currency-field">BRL</p>
      </header>
    );
  }
}

const mapStateToProps = ({ user, wallet }) => ({
  email: user.email,
  expenses: wallet.expenses,
});

Header.propTypes = {
  email: PropTypes.string,
}.isRequired;

export default connect(mapStateToProps)(Header);
