import React, { Component } from 'react';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';

class WalletForm extends Component {
  state = {
    despesas: '',
    description: '',
  };

  handlechange = ({ target: { name, value } }) => {
    this.setState({
      [name]: value,
    });
  };

  render() {
    const { description, despesas } = this.state;
    const { currencies } = this.props;
    return (
      <div>
        <form>
          <label htmlFor="despesas">
            Valor da Despesa:
            <input
              type="number"
              name="despesas"
              value={ despesas }
              data-testid="value-input"
              onChange={ this.handlechange }
            />
          </label>
          <label htmlFor="description">
            Descrição:
            <input
              type="text"
              name="description"
              value={ description }
              data-testid="description-input"
              onChange={ this.handlechange }
            />
          </label>
          <label htmlFor="moeda">
            Moeda:
            <select
              aria-label="moeda"
              name="moeda"
              id="moeda"
              data-testid="currency-input"
              onChange={ this.handlechange }
            >
              { currencies.map((element) => <option key={ element }>{element}</option>)}
            </select>
          </label>
          <label htmlFor="tipo-pgamento">
            Método de Pagamento:
            <select
              aria-label="tipo-pagamento"
              name="tipo-pagamento"
              data-testid="method-input"
              onChange={ this.handlechange }
            >
              <option value="Dinheiro">Dinheiro</option>
              <option value="Cartão de crédito">Cartão de crédito</option>
              <option value="Cartão de débito">Cartão de débito</option>
            </select>
          </label>
          <label htmlFor="tipo-gasto">
            Método de Pagamento:
            <select
              aria-label="tipo-gasto"
              name="method"
              data-testid="tag-input"
              onChange={ this.handlechange }
            >
              <option value="Alimentação">Alimentação</option>
              <option value="Lazer">Lazer</option>
              <option value="Trabalho">Trabalho</option>
              <option value="Transporte">Transporte</option>
              <option value="Saúde">Saúde</option>
            </select>
          </label>
        </form>
        <button type="button" onClick={ () => console.log({}) }>Adicionar despesa</button>
      </div>
    );
  }
}

const mapStateToProps = (state) => ({
  currencies: state.wallet.currencies,
});

WalletForm.propTypes = {
  currencies: PropTypes.shape(),
}.isRequired;

export default connect(mapStateToProps)(WalletForm);
